export function buildParamString(name, params, defaultParams) {
    const parts = [];
    for (const [name, value] of Object.entries(params || {})) {
        if (value) {
            if (defaultParams?.[name] != value) {
                parts.push(`${name}=${value}`);
            }
        }
    }
    if (parts.length) {
        return `${name}/${parts.join('/')}`;
    }
    else {
        return `${name}`;
    }
}
